String    WAVEFORM_label    = WAVEFORM_labels[WAVEFORM_index];

// Change waveform
void WAVEFORM_step(int steps) {

  // Step index with positive modulus.
  WAVEFORM_index += steps % WAVEFORM_count;
  if (WAVEFORM_index < 0) WAVEFORM_index += WAVEFORM_count;
  if (WAVEFORM_index >= WAVEFORM_count) WAVEFORM_index -= WAVEFORM_count;
  
  WAVEFORM_selected = WAVEFORM_waveforms[WAVEFORM_index];
  WAVEFORM_label    = WAVEFORM_labels[WAVEFORM_index];
}
